import{_ as l,o as i,c as t,R as r}from"./chunks/framework.XiqD54nH.js";const q=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"notes/公安重点内容.md","filePath":"notes/公安重点内容.md","lastUpdated":1709877978000}'),o={name:"notes/公安重点内容.md"},a=r('<h3 id="宪法" tabindex="-1">宪法 <a class="header-anchor" href="#宪法" aria-label="Permalink to &quot;宪法&quot;">​</a></h3><hr><h4 id="全人大职权" tabindex="-1">全人大职权 <a class="header-anchor" href="#全人大职权" aria-label="Permalink to &quot;全人大职权&quot;">​</a></h4><p>修宪监督制基本，三席三高三提名</p><p>改撤人常省建置，计划预算战与和</p><h4 id="全人常职权" tabindex="-1">全人常职权 <a class="header-anchor" href="#全人常职权" aria-label="Permalink to &quot;全人常职权&quot;">​</a></h4><p>督宪解释造非基，监督机关和撤销</p><p>动员特赦来开战，省国紧急和勋章</p><h3 id="行政执法依据" tabindex="-1">行政执法依据 <a class="header-anchor" href="#行政执法依据" aria-label="Permalink to &quot;行政执法依据&quot;">​</a></h3><hr><h4 id="辨认" tabindex="-1">辨认 <a class="header-anchor" href="#辨认" aria-label="Permalink to &quot;辨认&quot;">​</a></h4><ul><li>辨认违法<strong>嫌疑人</strong>：被辨认人数<strong>不得少于7人</strong></li><li>辨认违法嫌疑人<strong>照片</strong>：<strong>不得少于10人</strong>的照片</li><li>辨认<strong>物品</strong>：同类混杂物品<strong>不得少于5件</strong></li></ul><h4 id="处罚决定程序" tabindex="-1">处罚决定程序 <a class="header-anchor" href="#处罚决定程序" aria-label="Permalink to &quot;处罚决定程序&quot;">​</a></h4><ol><li><strong>简易程序</strong></li></ol><ul><li>适用条件 <ul><li>违法事实确凿</li><li>违反治安管理或道路交通安全行为人：<strong>200以下罚款、警告</strong></li><li>违反出入境管理行为人：500元以下罚款、警告</li></ul></li><li><strong>不适用：涉及卖淫、嫖娼、赌博、毒品</strong></li><li>程序 <ul><li>口头告知处罚的<strong>事实、理由和依据</strong>，享有陈述权和申述权</li><li><strong>填写当场处罚决定书</strong>并当场交付</li></ul></li><li>备案 <ul><li><strong>人民警察</strong>：作出决定的<strong>24小时</strong>内报所属公安机关备案</li><li><strong>交通警察</strong>：作出决定的<strong>2日</strong>内报所属公安机关交通管理部门备案</li></ul></li></ul><ol start="2"><li><strong>一般程序</strong></li></ol><ul><li>期限：受理之日起不得超过<strong>30日</strong>，案情重大、复杂的经<strong>上级公安机</strong>关批准，可以<strong>延长30日</strong></li><li>宣告与交付：向处罚人宣告治安管理处罚决定书并当场交付（无法当场宣告的，在<strong>2日</strong>内送达）</li></ul><ol start="3"><li><strong>快速办理程序</strong></li></ol><ul><li>适用条件 <ul><li><strong>不适用简易程序</strong></li><li>事实清楚</li><li>嫌疑人自愿认错认罚，无异议（仍需调查取证）</li></ul></li><li><strong>不适用</strong>情形 <ul><li>盲聋哑、精神病、未成年</li><li>依法应当适用听证程序</li><li>可能作出<strong>10日以上行政拘留</strong>处罚的</li></ul></li><li>快速办理期限：违法嫌疑人<strong>到案后48小时内</strong>作出决定</li></ul><h4 id="听证程序" tabindex="-1">听证程序 <a class="header-anchor" href="#听证程序" aria-label="Permalink to &quot;听证程序&quot;">​</a></h4><ul><li><strong>适用</strong>情形： <ul><li>责令停产停业</li><li>吊销许可证或执照</li><li>较大数额罚款（个人<strong>2000</strong>、单位<strong>10000</strong>、范围出入境个人6000以上）</li></ul></li></ul><h4 id="行政拘留" tabindex="-1">行政拘留 <a class="header-anchor" href="#行政拘留" aria-label="Permalink to &quot;行政拘留&quot;">​</a></h4><ul><li><p>场所：拘留所</p></li><li><p>期限</p><ul><li>单一执行：15日</li><li>合并执行：20日</li></ul></li><li><p><strong>暂缓执行</strong></p><ul><li>适用条件（<strong>同时满足</strong>） <ul><li>申请行政复议或行政诉讼</li><li>本人提出暂缓执行申请</li><li>公安机关认为暂缓执行不致发生社会危险</li><li>钱保（200/日）或人保</li></ul></li></ul></li><li><p>处罚不执行</p><ul><li>14≤年龄&lt;16</li><li>16≤年龄&lt;18，初次违反治安管理或其他公安行政管理</li><li>年龄≥70</li><li>怀孕或哺乳自己不满1周岁婴儿的</li></ul></li></ul><h3 id="职业素养" tabindex="-1">职业素养 <a class="header-anchor" href="#职业素养" aria-label="Permalink to &quot;职业素养&quot;">​</a></h3><hr><h4 id="人民警察职业道德规范" tabindex="-1">人民警察职业道德规范 <a class="header-anchor" href="#人民警察职业道德规范" aria-label="Permalink to &quot;人民警察职业道德规范&quot;">​</a></h4><ul><li>显著特征 <ul><li><strong>鲜明的阶级性</strong></li><li><strong>广泛的人民性</strong></li><li><strong>行为的表率性</strong></li></ul></li><li>主要内容：<strong>英文甘清团，热爱严秉忠</strong></li></ul><h4 id="公安部三项纪律" tabindex="-1">公安部三项纪律 <a class="header-anchor" href="#公安部三项纪律" aria-label="Permalink to &quot;公安部三项纪律&quot;">​</a></h4><ul><li>决不允许面对群众危难不勇为</li><li>决不允许酗酒滋事</li><li>决不允许进夜总会<strong>娱乐</strong></li></ul><h4 id="五项禁令" tabindex="-1">五项禁令 <a class="header-anchor" href="#五项禁令" aria-label="Permalink to &quot;五项禁令&quot;">​</a></h4><blockquote><p>违者：<strong>予以辞退</strong></p><p>严重后果：<strong>予以开除</strong></p></blockquote><ul><li>严禁违反枪支使用规定</li><li>严禁在工作时间饮酒</li><li>严禁携带枪支饮酒</li><li>严禁酒后驾驶机动车辆</li><li>严禁参与赌博</li></ul><h4 id="给予开除的行为" tabindex="-1">给予开除的行为 <a class="header-anchor" href="#给予开除的行为" aria-label="Permalink to &quot;给予开除的行为&quot;">​</a></h4><blockquote><p>口诀：<strong>放风逃不归 黄毒黑害国</strong></p></blockquote><ul><li>逃往境外或者非法出境、违反规定滞留境外不归的</li><li>参与、包庇或者纵容危害国家安全违法犯罪活动的</li><li>参与、包庇或者纵容黑社会性质组织犯罪活动的</li><li>向犯罪嫌疑人通风报信的</li><li>私放他人出入境的</li></ul><h4 id="人民警察核心价值观" tabindex="-1">人民警察核心价值观 <a class="header-anchor" href="#人民警察核心价值观" aria-label="Permalink to &quot;人民警察核心价值观&quot;">​</a></h4><ul><li><strong>忠诚</strong> （政治本色、根本要求）</li><li><strong>为民</strong> （宗旨要求、出发点落脚点）</li><li><strong>公正</strong> （价值追求）</li><li><strong>廉洁</strong>（内在要求、思想基础）</li></ul><h4 id="新时代建警治警的总方略" tabindex="-1">新时代建警治警的总方略 <a class="header-anchor" href="#新时代建警治警的总方略" aria-label="Permalink to &quot;新时代建警治警的总方略&quot;">​</a></h4><ul><li><strong>对党忠诚</strong>（建警之魂）</li><li><strong>服务人民</strong>（立警之本）</li><li><strong>执法公正</strong>（从警之基）</li><li><strong>纪律严明</strong>（治警之要）</li></ul><h3 id="公安基础知识" tabindex="-1">公安基础知识 <a class="header-anchor" href="#公安基础知识" aria-label="Permalink to &quot;公安基础知识&quot;">​</a></h3><hr><h4 id="公安机关的任务" tabindex="-1">公安机关的任务 <a class="header-anchor" href="#公安机关的任务" aria-label="Permalink to &quot;公安机关的任务&quot;">​</a></h4><ul><li><p><strong>总任务</strong>：<strong>维护国家安全和社会稳定</strong>（<strong>维稳</strong>）</p></li><li><p>基本任务：</p><blockquote><p>口诀：<strong>国社人 公私财 防惩犯</strong></p></blockquote><ul><li>维护国家安全（首要任务）</li><li>维护社会治安秩序和社会稳定（重要任务）</li><li>保护公民的人身安全和人身自由</li><li>保护公共财产和个人合法财产</li><li>预防、制止和惩治违法犯罪活动</li></ul></li><li><p><strong>新时代政法工作的四大职责任务</strong></p><ul><li>维护国家政治安全（首要任务）</li><li>确保社会大局稳定（基本任务）</li><li>促进社会公平正义（核心价值追求）</li><li>保障人民安居乐业（根本目标）</li></ul></li></ul><h4 id="公安工作" tabindex="-1">公安工作 <a class="header-anchor" href="#公安工作" aria-label="Permalink to &quot;公安工作&quot;">​</a></h4><ul><li><strong>根本原则</strong>：坚持党对公安工作的绝对领导</li><li><strong>根本路线</strong>：群众路线 <ul><li>一切为了群众（出发点和归宿、宗旨）</li><li>一切依靠群众（根本态度）</li><li>从群众中来，到群众中去（基本方法）</li></ul></li><li><strong>基本方针</strong>：在党委领导下的专门机关与广大群众相结合</li></ul><h4 id="继续盘问" tabindex="-1">继续盘问 <a class="header-anchor" href="#继续盘问" aria-label="Permalink to &quot;继续盘问&quot;">​</a></h4><ul><li>需要继续盘问的情形：<strong>指正嫌赃身不明</strong></li><li>时限（<strong>12 - 24 - 48</strong>）： <ul><li>一般12小时：<strong>派出所负责人</strong>批准</li><li>延长至24小时：县级以上公安机关<strong>值班负责人</strong>批准</li><li>延长至48小时：县级以上公安机关<strong>主管负责人</strong>批准</li></ul></li></ul>',47),n=[a];function s(e,g,u,h,d,c){return i(),t("div",null,n)}const p=l(o,[["render",s]]);export{q as __pageData,p as default};
